CREATE TABLE FRIDGE (number INT PRIMARY KEY, type VARCHAR (255), description VARCHAR (255), discount BOOLEAN, defect BOOLEAN, price DECIMAL, energy VARCHAR (255), registered TIMESTAMP WITH TIME ZONE);
CREATE TABLE KETTLE (number INT PRIMARY KEY, type VARCHAR (255), color VARCHAR (255), isElectric BOOLEAN, isInduction BOOLEAN, price DECIMAL, energy VARCHAR (255), registered TIMESTAMP WITH TIME ZONE);
CREATE TABLE MULTICOOKER (number INT PRIMARY KEY, type VARCHAR (255), description VARCHAR (255), isTouchScreen BOOLEAN, numberModes INT, price DECIMAL, energy VARCHAR (255), registered TIMESTAMP WITH TIME ZONE);
CREATE TABLE TV (number INT PRIMARY KEY, type VARCHAR (255), brand VARCHAR (255), discount BOOLEAN, diagonal INT, price DECIMAL, energy VARCHAR (255), registered TIMESTAMP WITH TIME ZONE);
